!function(){let navLinks=document.querySelectorAll(".nav-link"),statsDiv=document.querySelectorAll(".main-stats")[0],leaguesDiv=document.querySelectorAll(".live-leagues")[0];const liveLeaguesTable=document.querySelectorAll(".live-leagues-table")[0];navLinks.forEach(function(link){link.addEventListener("click",event=>{navLinks.forEach(function(link){link.classList.remove("active")}),event.preventDefault();let target=event.target.parentNode;target.classList.add("active"),target.classList.contains("stats")?(statsDiv.classList.remove("hidden"),leaguesDiv.classList.add("hidden")):(leaguesDiv.classList.remove("hidden"),liveLeaguesTable.classList.remove("animated"),liveLeaguesTable.classList.remove("fadeOutDown"),statsDiv.classList.add("hidden"))})})}();